import re
import pandas as pd
from youtube_transcript_api import NoTranscriptFound, TranscriptsDisabled, YouTubeTranscriptApi

from lfx.custom.custom_component.component import Component
from lfx.inputs.inputs import DropdownInput, IntInput, MultilineInput
from lfx.schema.data import Data
from lfx.schema.dataframe import DataFrame
from lfx.schema.message import Message
from lfx.template.field.base import Output


class YouTubeTranscriptsComponent(Component):
    """
    A component that extracts spoken content from YouTube videos as transcripts.
    Supports automatic language detection and translation.
    """

    display_name: str = "YouTube Transcripts"
    description: str = "Extracts spoken content from YouTube videos with multiple output options."
    icon: str = "YouTube"
    name = "YouTubeTranscripts"

    inputs = [
        MultilineInput(
            name="url",
            display_name="Video URL",
            info="Enter the YouTube video URL to get transcripts from.",
            tool_mode=True,
            required=True,
        ),
        IntInput(
            name="chunk_size_seconds",
            display_name="Chunk Size (seconds)",
            value=60,
            info="The size of each transcript chunk in seconds.",
        ),
        DropdownInput(
            name="translation",
            display_name="Translation Language",
            advanced=True,
            options=["", "en", "es", "fr", "de", "it", "pt", "ru", "ja", "ko", "hi", "ar", "id"],
            info="Translate the transcripts to the specified language. Leave empty for no translation.",
        ),
    ]

    outputs = [
        Output(name="dataframe", display_name="Chunks", method="get_dataframe_output"),
        Output(name="message", display_name="Transcript", method="get_message_output"),
        Output(name="data_output", display_name="Transcript + Source", method="get_data_output"),
    ]

    def _extract_video_id(self, url: str) -> str:
        """Extract video ID from various YouTube URL formats."""
        patterns = [
            r"(?:youtube\.com\/watch\?v=|youtu\.be\/|youtube\.com\/embed\/)([^&\n?#]+)",
            r"youtube\.com\/watch\?.*?v=([^&\n?#]+)",
        ]
        for pattern in patterns:
            match = re.search(pattern, url)
            if match:
                return match.group(1)
        msg = f"Could not extract video ID from URL: {url}"
        raise ValueError(msg)

    def _get_val(self, segment, key):
        """Helper to handle both dictionary and object attribute access safely."""
        if isinstance(segment, dict):
            return segment.get(key)
        return getattr(segment, key, None)

    def _load_transcripts(self, *, as_chunks: bool = True):
        """Internal method to load and process transcripts with object-attribute compatibility."""
        try:
            video_id = self._extract_video_id(self.url)
            api = YouTubeTranscriptApi()
            transcript_list = api.list(video_id)

            # Strategy: Find Portuguese or English, or fallback to any available
            try:
                transcript = transcript_list.find_transcript(['pt', 'en'])
            except NoTranscriptFound:
                transcript = next(iter(transcript_list))

            # Only translate if target is different from original language
            if self.translation and self.translation != transcript.language_code:
                if transcript.is_translatable:
                    transcript = transcript.translate(self.translation)

            transcript_data = transcript.fetch()

        except Exception as e:
            raise RuntimeError(f"Error retrieving transcript: {str(e)}")

        if as_chunks:
            return self._chunk_transcript(transcript_data)
        return transcript_data

    def _chunk_transcript(self, transcript_data):
        """Groups individual transcript segments into time-based blocks using safe access."""
        chunks = []
        current_chunk = []
        chunk_start = 0

        for segment in transcript_data:
            segment_start = self._get_val(segment, "start")

            if chunk_start is None:
                chunk_start = segment_start

            if segment_start - chunk_start >= self.chunk_size_seconds and current_chunk:
                chunk_text = " ".join(self._get_val(s, "text") for s in current_chunk)
                chunks.append({"start": chunk_start, "text": chunk_text})
                current_chunk = []
                chunk_start = segment_start

            current_chunk.append(segment)

        if current_chunk:
            chunk_text = " ".join(self._get_val(s, "text") for s in current_chunk)
            chunks.append({"start": chunk_start, "text": chunk_text})

        return chunks

    def get_dataframe_output(self) -> DataFrame:
        """Returns the transcript formatted as a Pandas DataFrame."""
        try:
            chunks = self._load_transcripts(as_chunks=True)
            data = []
            for chunk in chunks:
                start_seconds = int(chunk["start"])
                timestamp = f"{start_seconds // 60:02d}:{start_seconds % 60:02d}"
                data.append({"timestamp": timestamp, "text": chunk["text"]})
            return DataFrame(pd.DataFrame(data))
        except Exception as exc:
            return DataFrame(pd.DataFrame({"error": [str(exc)]}))

    def get_message_output(self) -> Message:
        """Returns the full transcript as a single text message."""
        try:
            transcript_data = self._load_transcripts(as_chunks=False)
            result = " ".join(self._get_val(segment, "text") for segment in transcript_data)
            return Message(text=result)
        except Exception as exc:
            return Message(text=str(exc))

    def get_data_output(self) -> Data:
        """Returns a structured Data object containing transcript and source metadata."""
        try:
            transcript_data = self._load_transcripts(as_chunks=False)
            full_transcript = " ".join(self._get_val(segment, "text") for segment in transcript_data)
            return Data(data={"transcript": full_transcript, "video_url": self.url})
        except Exception as exc:
            return Data(data={"transcript": "", "video_url": self.url, "error": str(exc)})

from lfx.components.processing.converter import convert_to_data
from lfx.custom.custom_component.component import Component
from lfx.inputs.inputs import HandleInput
from lfx.schema.data import Data
from lfx.schema.dataframe import DataFrame
from lfx.schema.message import Message
from lfx.template.field.base import Output


class LoopComponent(Component):
    display_name = "Loop"
    description = (
        "Iterates over a list of Data or Message objects, outputting one item at a time and "
        "aggregating results from loop inputs. Message objects are automatically converted to "
        "Data objects for consistent processing."
    )
    documentation: str = "https://docs.langflow.org/loop"
    icon = "infinity"

    inputs = [
        HandleInput(
            name="data",
            display_name="Inputs",
            info="The initial DataFrame to iterate over.",
            input_types=["DataFrame"],
        ),
    ]

    outputs = [
        Output(
            display_name="Item",
            name="item",
            method="item_output",
            allows_loop=True,
            loop_types=["Message"],
            group_outputs=True,
        ),
        Output(display_name="Done", name="done", method="done_output", group_outputs=True),
    ]

    def initialize_data(self) -> None:
        """Initialize the data list, context index, and aggregated list."""
        if self.ctx.get(f"{self._id}_initialized", False):
            return

        # Ensure data is a list of Data objects
        data_list = self._validate_data(self.data)

        # Store the initial data and context variables
        self.update_ctx(
            {
                f"{self._id}_data": data_list,
                f"{self._id}_index": 0,
                f"{self._id}_aggregated": [],
                f"{self._id}_initialized": True,
            }
        )

    def _convert_message_to_data(self, message: Message) -> Data:
        """Convert a Message object to a Data object using Type Convert logic."""
        return convert_to_data(message, auto_parse=False)

    def _validate_data(self, data):
        """Validate and return a list of Data objects. Message objects are auto-converted to Data."""
        if isinstance(data, DataFrame):
            return data.to_data_list()
        if isinstance(data, Data):
            return [data]
        if isinstance(data, Message):
            # Auto-convert Message to Data
            converted_data = self._convert_message_to_data(data)
            return [converted_data]
        if isinstance(data, list) and all(isinstance(item, (Data, Message)) for item in data):
            # Convert any Message objects in the list to Data objects
            converted_list = []
            for item in data:
                if isinstance(item, Message):
                    converted_list.append(self._convert_message_to_data(item))
                else:
                    converted_list.append(item)
            return converted_list
        msg = "The 'data' input must be a DataFrame, a list of Data/Message objects, or a single Data/Message object."
        raise TypeError(msg)

    def evaluate_stop_loop(self) -> bool:
        """Evaluate whether to stop item or done output."""
        current_index = self.ctx.get(f"{self._id}_index", 0)
        data_length = len(self.ctx.get(f"{self._id}_data", []))
        return current_index > data_length

    def item_output(self) -> Data:
        """Output the next item in the list or stop if done."""
        self.initialize_data()
        current_item = Data(text="")

        if self.evaluate_stop_loop():
            self.stop("item")
        else:
            # Get data list and current index
            data_list, current_index = self.loop_variables()
            if current_index < len(data_list):
                # Output current item and increment index
                try:
                    current_item = data_list[current_index]
                except IndexError:
                    current_item = Data(text="")
            self.aggregated_output()
            self.update_ctx({f"{self._id}_index": current_index + 1})

        # Now we need to update the dependencies for the next run
        self.update_dependency()
        return current_item

    def update_dependency(self):
        item_dependency_id = self.get_incoming_edge_by_target_param("item")
        if item_dependency_id not in self.graph.run_manager.run_predecessors[self._id]:
            self.graph.run_manager.run_predecessors[self._id].append(item_dependency_id)
            # CRITICAL: Also update run_map so remove_from_predecessors() works correctly
            # run_map[predecessor] = list of vertices that depend on predecessor
            if self._id not in self.graph.run_manager.run_map[item_dependency_id]:
                self.graph.run_manager.run_map[item_dependency_id].append(self._id)

    def done_output(self) -> DataFrame:
        """Trigger the done output when iteration is complete."""
        self.initialize_data()

        if self.evaluate_stop_loop():
            self.stop("item")
            self.start("done")

            aggregated = self.ctx.get(f"{self._id}_aggregated", [])

            return DataFrame(aggregated)
        self.stop("done")
        return DataFrame([])

    def loop_variables(self):
        """Retrieve loop variables from context."""
        return (
            self.ctx.get(f"{self._id}_data", []),
            self.ctx.get(f"{self._id}_index", 0),
        )

    def aggregated_output(self) -> list[Data]:
        """Return the aggregated list once all items are processed.

        Returns Data or Message objects depending on loop input types.
        """
        self.initialize_data()

        # Get data list and aggregated list
        data_list = self.ctx.get(f"{self._id}_data", [])
        aggregated = self.ctx.get(f"{self._id}_aggregated", [])
        loop_input = self.item

        # Append the current loop input to aggregated if it's not already included
        if loop_input is not None and not isinstance(loop_input, str) and len(aggregated) <= len(data_list):
            # If the loop input is a Message, convert it to Data for consistency
            if isinstance(loop_input, Message):
                loop_input = self._convert_message_to_data(loop_input)
            aggregated.append(loop_input)
            self.update_ctx({f"{self._id}_aggregated": aggregated})
        return aggregated
